import './App.css';
import data from './resume.csv';
import { csv } from 'd3';
import React, {useEffect, useState} from 'react';
import axios from 'axios';

function App() {

  const [filteredData, setFilteredData] = useState([]);
  const [job, setJob] = useState('');
  const [location, setLocation] = useState('');
  const [display, setDisplay] = useState('');
  
  useEffect(() => {
    csv(data).then(data => {
      setFilteredData(data)
    })
  }, [])


  const [indeed, setIndeed] = React.useState('hi');

  useEffect(() => {
    console.log('hi')
    fetch("/api")
      .then((res) => res.json())
      .then((data) => setIndeed(data.message));
  }, []);


  // filter results
  var submit = (e) => {
    console.log('job', job);
    console.log('loc', location);
    setDisplay('')
    // filter by job first
    let temp = filteredData.filter(each => each.Job === job);
    if (temp.length === 0) {
      setDisplay("No jobs found");
      return;
    }

    temp = temp.filter(each => each.Location === location);
    if (temp.length === 0) {
      setDisplay("No Location found");
      return;
    }
    setDisplay('')
    // send job to API
    // send location to API
    
    axios.post('localhost:3001/api', {job: job, location: location})
      .then(res => {
        console.log(res);
        console.log(res.data);
      })


    // fetch('./api', {method: 'POST', 
    //                 headers: { 'Content-Type': 'application/json' },
    //                 body: JSON.stringify({ job: job, location: location })})
    //   .then((res) => console.log('hi'))
    //   .then((data) => console.log('hi'));


  }

  console.log(filteredData[0]);

  return (
    <div >
        <label for="job">Job:</label>
        <input type="text" id="job" onChange={(e) => setJob(e.target.value)} ></input>
        <label for="loc">Location:</label>
        <input type="text" id="loc" name="loc" onChange={(e) => setLocation(e.target.value)}></input>
        <button type="button" onClick={submit}>Click Me!</button>

        <h3>{indeed}</h3>
        <h3>{display}</h3>
    </div>
  );
}

export default App;
